class Fraction:    def __init__(self,numertor,denumerator):        self.numertor = numertor        self.denumerator = denumerator    def GCD(self,a,b):        return self.GCD(b, a % b) if b else a    def __add__(self, other):        znam = self.denumerator * other.denumerator // self.GCD(self.denumerator, other.denumerator)        chis = znam // self.denumerator * self.numertor + znam // other.denumerator * other.numertor        self.numertor = chis        self.denumerator = znam        return Fraction(denumerator = self.denumerator, numertor = self.numertor)    def __sub__(self, other):        znam = self.denumerator * other.denumerator // self.GCD(self.denumerator, other.denumerator)        chis = znam // self.denumerator * self.numertor - znam // other.denumerator * other.numertor        self.numertor = chis        self.denumerator = znam        return Fraction(denumerator=self.denumerator, numertor=self.numertor)    def __mul__(self, other):        chis = self.numertor  * other.numertor        znam = self.denumerator * other.denumerator        u = self.GCD(chis , znam)        chis //= u        znam //= u        self.numertor = chis        self.denumerator = znam        return Fraction(denumerator=self.denumerator, numertor=self.numertor)    def __floordiv__(self, other):        d = other.numertor        other.numertor = other.denumerator        other.denumerator = d        chis = self.numertor * other.numertor        znam = self.denumerator * other.denumerator        u = self.GCD(chis, znam)        chis //= u        znam //= u        self.numertor = chis        self.denumerator = znam        return Fraction(numertor=self.numertor, denumerator=self.denumerator)    def __str__(self):        return f"{self.numertor}/{self.denumerator}"num1 = input("Введите дробь в формате ?/?: ")num2 = input("Введите дробь в формате ?/?: ")num1 = num1.split('/')num2 = num2.split('/')a = Fraction(int(num1[0]), int(num1[1]))b = F